.footer{
  background: url(../images/footer-bg.jpg) no-repeat;
  background-size: cover;
  background-position: center;
}

.footer .box-container{
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(25rem, 1fr));
  gap: 3rem;
}

.footer .box-container .box h3{
  color:var(--main-color);
  font-size: 2.5rem;
  padding-bottom: 2rem;
}

.footer .box-container .box a{
  color:var(--white);
  font-size: 1.5rem;
  padding-bottom: 1.5rem;
  display: block;
}

.footer .box-container .box a i{
  color:var(--main-color);
  padding-right: .5rem;
  transition: .2s linear;
}

.footer .box-container .box a:hover i{
  padding-right: 2rem;
}

.footer .credit{
  text-align: center;
  padding-top: 3rem;
  margin-top: 3rem;
  border-top: .1rem solid var(--light-white);
  font-size: 2rem;
  color:var(--white);
}

.footer .credit span{
  color:var(--main-color);
}




.reviews .slide{
  padding:2rem;
  border:var(--border);
  background: var(--white);
  text-align: center;
  box-shadow: var(--box-shadow);
  user-select: none;
}

.reviews .slide .stars{
  padding-bottom: .5rem;
}

.reviews .slide .stars i{
  font-size: 1.7rem;
  color:var(--main-color);
}

.reviews .slide p{
  font-size: 1.5rem;
  color:var(--light-black);
  line-height: 2;
  padding:1rem 0;
}

.reviews .slide h3{
  font-size: 2rem;
  color:var(--black);
}

.reviews .slide span{
  font-size: 1.5rem;
  color:var(--main-color);
  display: block;
}

.reviews .slide img{
  height: 7rem;
  width: 7rem;
  border-radius: 50%;
  margin-top: 1rem;
}
